import React from "react";

const ForgotPassword = () => {
  return (
    <div className="flex h-screen">
      {/* Left Side - Blue Section */}
      <div className="hidden md:flex flex-col justify-center items-center w-1/2 bg-blue-600 text-white p-10">
        <img src="https://your-logo-url.com/logo.png" alt="Logo" className="w-32 mb-4" />
        <p className="text-center text-lg">A DESKTOP APPLICATION TO SIMPLIFY PFE MANAGEMENT</p>
        <div className="mt-10 text-sm opacity-80">École Supérieure en Informatique - Sidi Bel Abbès</div>
      </div>

      {/* Right Side - Form Section */}
      <div className="w-full md:w-1/2 flex flex-col justify-center items-center p-8">
        <div className="max-w-md w-full">
          <h2 className="text-3xl font-bold text-gray-800">Forget password?</h2>
          <p className="text-gray-500 mt-2">
            We will send a recovery link to this email, please make sure to follow the instructions to get access again.
          </p>

          {/* Email Input */}
          <div className="mt-6">
            <label className="block text-gray-700 mb-2">Email address</label>
            <input
              type="email"
              placeholder="Enter your email"
              className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>

          {/* Buttons */}
          <div className="mt-6 flex justify-between items-center">
            <a href="/login" className="text-blue-600 hover:underline text-sm">Back to login</a>
            <button className="bg-blue-600 text-white px-5 py-2 rounded-lg hover:bg-blue-700 transition">
              Send recovery link
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ForgotPassword;
