import React from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate for navigation
import "../styles/App.css";
import logo from "../logo.svg";
import schoolIcon from "../school-icon.svg";

const ForgotPassword = () => {
  const navigate = useNavigate(); // Hook to handle navigation

  const handleConfirmClick = () => {
    // Simulate an API call to send the email (if needed)
    // After confirming, navigate to the "Check Email" page
    navigate("/CheckEmail");
  };

  return (
    <div className="login-container">
      {/* Left Side (No Changes) */}
      <div className="login-left">
        <div className="logo">
          <img src={logo} alt="PFE Logo" />
        </div>
        <img className="school-logo" src={schoolIcon} alt="ESI School Logo" />
      </div>

      {/* Right Side (Updated) */}
      <div className="login-right">
        <div className="right-content">
          <h1>Forget password?</h1>
          <p>
            We will send a recovery link to this email, please make sure to follow the instructions  to get access again.
          </p>
          <form>

            <label>Email address</label>
            <div className="email-field"><input type="email" placeholder="" className="input-field" /></div>





            <div className="check-email-actions">
        {/* "Back to log in" Button */}
        <button className="link-btn" id="" onClick={() => navigate("/")}>
        Back to log in
        </button>

        {/* "Back to log in" Button */}
        <button className="btn" id="checkEmail-btn" onClick={() => navigate("/CheckEmail")}>
        send recovery link
        </button>
        </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default ForgotPassword;
