import React from "react";
import "./Login.css";
import logo from "../logo.svg";
import schoolIcon from "../school-icon.svg";

const ForgotPassword = () => {
  return (
    <div className="login-container">
      {/* Left Side (Same as Login Page) */}
      <div className="login-left">
        <div className="logo">
          <img src={logo} alt="PFE Logo" />
        </div>
        <img className="school-logo" src={schoolIcon} alt="ESI School Logo" />
      </div>

      {/* Right Side (Forgot Password Content) */}
      <div className="login-right">
        <div>
          <h1>Forget password?</h1>
          <p>
            To reset your password, you need to confirm that you lost your password.
            By clicking the button below, an email will be sent to your university email
            address, with instructions on how to regain access.
          </p>
        </div>

        <button className="sign-in-btn">Confirm</button>
      </div>
    </div>
  );
};

export default ForgotPassword;
