import React, { useState } from "react";
import "../styles/App.css";
import logo from "../logo.svg";
import schoolIcon from "../school-icon.svg";
import { FaRegEye, FaRegEyeSlash } from "react-icons/fa";
import { Link } from "react-router-dom";

const Login = () => {
  const [showPassword, setShowPassword] = useState(false);

  return (
    <div className="login-container">
      {/* Left Side (No Changes) */}
      <div className="login-left">
        <div className="logo">
          <img src={logo} alt="PFE Logo" />
        </div>
        <img className="school-logo" src={schoolIcon} alt="ESI School Logo" />
      </div>

      {/* Right Side (Updated) */}
      <div className="login-right">
        <div className="right-content">
          <h1>Welcome back!</h1>
          <p>
            Simplify your PFE journey with an all-in-one platform to manage, track, and
            organize your final project.
          </p>
          <form>

            <label>Email address</label>
            <div className="email-field"><input type="email" placeholder="" className="input-field" /></div>

            <label >Password</label>
            <div className="password-field">
              <input
                type={showPassword ? "text" : "password"}
                placeholder=""
                className="input-field"
              />
              <span onClick={() => setShowPassword(!showPassword)}>
                {showPassword ? <FaRegEyeSlash /> : <FaRegEye />}

              </span>
            </div>

            <div className="remember-forgot">
              <label>
                <input type="checkbox" /> Remember me
              </label>
              <Link to="/ForgotPassword">Forgot password?</Link>
            </div>

            <button type="submit" className="btn">Login in</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
