import React from "react";
import { useNavigate } from "react-router-dom";
import "./CheckEmail.css"; // Make sure this file has the correct styles

const CheckEmail = () => {
  const navigate = useNavigate(); // Navigation hook

  const handleResendLink = () => {
    alert("A new recovery link has been sent to your email.");
  };

  return (
    <div className="check-email-container">
      <h1>Check your email.</h1>
      <p>
        We just sent a recovery link to <strong>mn.guessoum@gmail.com</strong>. 
        If you canâ€™t find it in your inbox, please check your spam filter.
      </p>

      {/* Resend Link Styled as a Button */}
      <button className="resend-btn" onClick={handleResendLink}>
        Resent recovery link
      </button>

      {/* Back to Login Button */}
      <button className="back-btn" onClick={() => navigate("/login")}>
        Back to log in
      </button>
    </div>
  );
};

export default CheckEmail;
