import React from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate for navigation
import "../styles/App.css";
import logo from "../logo.svg";
import schoolIcon from "../school-icon.svg";

const ForgotPassword = () => {
  const navigate = useNavigate(); // Hook to handle navigation

  const handleConfirmClick = () => {
    // Simulate an API call to send the email (if needed)
    // After confirming, navigate to the "Check Email" page
    navigate("/CheckEmail");
  };

  return (
    <div className="forgot-password-container">
      {/* Left Side */}
      <div className="forgot-left">
        <div className="logo">
          <img src={logo} alt="PFE Logo" />
        </div>
        <img className="school-logo" src={schoolIcon} alt="ESI School Logo" />
      </div>

      {/* Right Side */}
      <div className="forgot-right">
        <div className="forgot-content">
          <h1 className="forgot-title">Forgot Password?</h1>
          <p className="forgot-text">
            To reset your password, please confirm that you have lost it. By clicking
            the button below, an email will be sent to your university email
            address with instructions on how to regain access.
          </p>
          <button className="forgot-btn" onClick={handleConfirmClick}>Confirm</button>
        </div>
      </div>
    </div>
  );
};

export default ForgotPassword;
