import React, { useState } from "react";
import "../styles/App.css";
import logo from "../logo.svg";
import schoolIcon from "../school-icon.svg";

const ResetPassword = () => {
  const [password, setPassword] = useState("");
  const [passwordStrength, setPasswordStrength] = useState("");

  const checkPasswordStrength = (value) => {
    setPassword(value);
    const strongPassword = /^(?=.*[A-Z])(?=.*[0-9!@#$%^&*])[A-Za-z0-9!@#$%^&*]{8,12}$/;
    if (strongPassword.test(value)) {
      setPasswordStrength("Strong");
    } else {
      setPasswordStrength("Weak");
    }
  };

  return (
    <div className="reset-password-container">
      {/* Left Side */}
      <div className="reset-left">
        <div className="logo">
          <img src={logo} alt="PFE Logo" />
        </div>

        <img className="school-logo" src={schoolIcon} alt="ESI School Logo" />
      </div>

      {/* Right Side */}
      <div className="reset-right">
        <div className="reset-content">
          <h1>Reset password.</h1>
          <p>
            Simplify your PFE journey with an all-in-one platform to manage,
            track, and organize your final project.
          </p>
          <form>
            <label>New password</label>
            <div className="password-field">
              <input
                type="password"
                value={password}
                onChange={(e) => checkPasswordStrength(e.target.value)}
                placeholder=""
                className="input-field"
              />
            </div>
            <p className={`password-strength ${passwordStrength.toLowerCase()}`}>
              Password strength: {passwordStrength || "Enter a password"}
            </p>
            <ul className="password-requirements">
              <li>
                {password.length >= 8 && password.length <= 12 ? "✓" : "✗"} Minimum 8-12 characters
              </li>
              <li>
                {/[A-Z]/.test(password) ? "✓" : "✗"} At least one uppercase letter
              </li>
              <li>
                {/[0-9!@#$%^&*]/.test(password) ? "✓" : "✗"} At least one number or symbol
              </li>
              <li>{!/\s/.test(password) ? "✓" : "✗"} Cannot contain spaces</li>
            </ul>
            <button type="submit" className="btn">
              Reset password
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default ResetPassword;
